@push('script')
    <script src="https://unpkg.com/aos@next/dist/aos.js"></script>
    <script>
        function selectTemplate(templateId) {
            document.getElementById('templateId').value = templateId;

            // Update the border color and checkmark for the selected template
            document.querySelectorAll('.templateGrid__box').forEach(function (el) {
                el.style.border = '1px solid #f8f8f8';
            });
            document.querySelector('.template-box-' + templateId).style.border = '1px solid #F16645';

            // Enable the submit button
            document.getElementById('submit-button').disabled = false;
        }


        AOS.init({
            once: true,
        })
    </script>
@endpush
